mapper.select:
SELECT


,wuzi.wuzi_name as wuziName
,wuzi.wuzi_uuid_number as wuziUuidNumber
,wuzi.wuzi_photo as wuziPhoto
,wuzi.wuzi_types as wuziTypes
,wuzi.wuzi_content as wuziContent
,wuzi.wuzi_delete as wuziDelete
,yonghu.yonghu_name as yonghuName
,yonghu.yonghu_phone as yonghuPhone
,yonghu.yonghu_id_number as yonghuIdNumber
,yonghu.yonghu_photo as yonghuPhoto
,yonghu.yonghu_email as yonghuEmail

FROM wuzifafang  a
left JOIN wuzi wuzi ON a.wuzi_id = wuzi.id
left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



        and a.id in

        #{item}



        and (
            a.wuzi_id = #{params.wuziId}
        )


        and (
            a.yonghu_id = #{params.yonghuId}
        )


          and a.wuzifafang_shuliang >= #{params.wuzifafangShuliangStart}   


          and a.wuzifafang_shuliang <= #{params.wuzifafangShuliangEnd}   


        and a.wuzifafang_shuliang = #{params.wuzifafangShuliang}


        and a.wuzifafang_content like CONCAT('%',#{params.wuzifafangContent},'%')


          and a.wuzifafang_delete >= #{params.wuzifafangDeleteStart}   


          and a.wuzifafang_delete <= #{params.wuzifafangDeleteEnd}   


        and a.wuzifafang_delete = #{params.wuzifafangDelete}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




        and a.wuzi_id IS NOT NULL


        and wuzi.wuzi_name like CONCAT('%',#{params.wuziName},'%')


        and wuzi.wuzi_uuid_number like CONCAT('%',#{params.wuziUuidNumber},'%')


        and wuzi.wuzi_types = #{params.wuziTypes}



        and wuzi.wuzi_content like CONCAT('%',#{params.wuziContent},'%')


          and wuzi.wuzi_delete >= #{params.wuziDeleteStart}   


          and wuzi.wuzi_delete <= #{params.wuziDeleteEnd}   


        and wuzi.wuzi_delete = #{params.wuziDelete}


          and UNIX_TIMESTAMP(wuzi.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(wuzi.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



        and a.yonghu_id IS NOT NULL


        and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


        and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


        and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


        and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


order by a.${params.sort} ${params.order}
